// ENRICHMENT: Combine AI-generated content with existing restaurant data
// Get the existing restaurant data from database
const existingRestaurant = $input.first().json;

// Get AI-generated content from OpenAI
const aiDescription = $input.all()[1]?.json?.choices?.[0]?.message?.content || '';
const aiVibes = $input.all()[2]?.json?.choices?.[0]?.message?.content || '';

console.log('=== ENRICHMENT PROCESS ===');
console.log('Existing restaurant from DB:', existingRestaurant);
console.log('AI Description:', aiDescription);
console.log('AI Vibes:', aiVibes);

// ENRICHMENT LOGIC: Preserve existing data, enhance with AI
const enrichedData = {
  // PRESERVE existing data from database
  id: existingRestaurant.id,
  name: existingRestaurant.name,
  address: existingRestaurant.address,
  neighborhood: existingRestaurant.neighborhood,
  cuisine: existingRestaurant.cuisine,
  price_range: existingRestaurant.price_range,
  rating: existingRestaurant.rating || 4.0,
  latitude: existingRestaurant.latitude,
  longitude: existingRestaurant.longitude,
  city: existingRestaurant.city,
  state: existingRestaurant.state,

  // ENRICH description: Use AI-generated if available, fallback to existing
  description: enrichDescription(existingRestaurant.description || '', aiDescription, existingRestaurant),

  // ENRICH vibe: Use AI-generated vibes + existing vibes
  vibe: enrichVibes(existingRestaurant.vibe || [], aiVibes, existingRestaurant),

  // ENRICH menu highlights: Keep existing
  menu_highlights: existingRestaurant.menu_highlights || [],

  // ENRICH images: Keep existing
  images: existingRestaurant.images || [],

  last_enhanced: new Date().toISOString()
};

console.log('=== ENRICHMENT RESULTS ===');
console.log('Final enriched data:', enrichedData);
console.log('Final rating:', enrichedData.rating);
console.log('Images count:', enrichedData.images.length);
console.log('Vibes count:', enrichedData.vibe.length);
console.log('Menu highlights count:', enrichedData.menu_highlights.length);

// Helper functions for enrichment
function enrichDescription(existingDesc, aiDesc, restaurant) {
  console.log('Enriching description:');
  console.log('- Existing description from DB:', existingDesc?.substring(0, 50) + '...');
  console.log('- AI-generated description:', aiDesc?.substring(0, 50) + '...');

  // If we have AI-generated description, use it
  if (aiDesc && aiDesc.trim() !== '') {
    console.log('- Using AI-generated description');
    return aiDesc.trim();
  }

  // Fallback to existing description
  if (existingDesc && existingDesc.trim() !== '') {
    console.log('- Using existing description from DB');
    return existingDesc;
  }

  // Final fallback
  const fallbackDesc = `${restaurant.cuisine || 'Restaurant'} in ${restaurant.neighborhood || 'NYC'}`;
  console.log('- Using fallback description:', fallbackDesc);
  return fallbackDesc;
}

function enrichVibes(existingVibes, aiVibes, restaurant) {
  const enriched = [...(existingVibes || [])];

  console.log('Enriching vibes:');
  console.log('- Existing vibes from DB:', existingVibes);
  console.log('- AI-generated vibes:', aiVibes);

  // Add AI-generated vibes if available
  if (aiVibes && aiVibes.trim() !== '') {
    const aiVibeArray = aiVibes.split(',').map(v => v.trim()).filter(Boolean);
    console.log('- Adding AI-generated vibes:', aiVibeArray);
    enriched.push(...aiVibeArray);
  }

  // Add neighborhood-based vibes
  if (restaurant.neighborhood === 'Manhattan' && !enriched.includes('Manhattan')) {
    enriched.push('Manhattan');
    console.log('- Added: Manhattan');
  }

  // Add cuisine-based vibes
  if (restaurant.cuisine && !enriched.includes(restaurant.cuisine)) {
    enriched.push(restaurant.cuisine);
    console.log('- Added cuisine:', restaurant.cuisine);
  }

  // Remove duplicates and limit to 8 vibes
  const finalVibes = [...new Set(enriched)].slice(0, 8);
  console.log('- Final vibes:', finalVibes);

  return finalVibes;
}

return enrichedData;

